package idris.com.yiling_plugin.wty.nrdemo.util;/* * Create 7/18 by xwj * 数据处理，进制转换等常用工具类 */public class DataTreater {    public static short byteToShort(byte high, byte low) {        return (short) (((high << 8) & 0xFF00) | (low & 0xFF));    }    public static short byteToShort2(byte high, byte low) {        return (short) (((high << 8) & 0xFF00) | (low & 0xFF));    }}